import { useState } from "react";
import { MessageCircle, X } from "lucide-react";
import { SiIndiamart } from "react-icons/si";
import { Button } from "@/components/ui/button";

export default function FloatingContactWidget() {
  const [isExpanded, setIsExpanded] = useState(false);

  return (
    <div className="fixed bottom-6 left-6 z-40 flex flex-col items-start gap-3">
      {/* Expanded Links */}
      {isExpanded && (
        <div className="flex flex-col gap-2 animate-in slide-in-from-bottom-5 duration-300">
          {/* WhatsApp Link */}
          <a
            href="https://wa.me/919568572005"
            target="_blank"
            rel="noopener noreferrer"
            className="group flex items-center gap-3 bg-[#25D366] hover:bg-[#20BA5A] text-white px-4 py-3 rounded-full shadow-lg transition-all duration-300 hover:scale-105"
            data-testid="link-whatsapp-widget"
          >
            <MessageCircle className="h-5 w-5 flex-shrink-0" />
            <span className="text-sm font-medium whitespace-nowrap">
              Chat on WhatsApp
            </span>
          </a>

          {/* IndiaMART Link */}
          <a
            href="https://www.indiamart.com/dls-envirotech-corporation/"
            target="_blank"
            rel="noopener noreferrer"
            className="group flex items-center gap-3 bg-[#E97D27] hover:bg-[#D96F1F] text-white px-4 py-3 rounded-full shadow-lg transition-all duration-300 hover:scale-105"
            data-testid="link-indiamart-widget"
          >
            <SiIndiamart className="h-5 w-5 flex-shrink-0" />
            <span className="text-sm font-medium whitespace-nowrap">
              View on IndiaMART
            </span>
          </a>
